$(document).ready(function(){
  $('.slider__inner').slick({
    arrows: true,
    dots: true,
    slidesToShow: 3,
    speed: 1000,
    infinite: true,
    pauseOnHover: true,
    pauseOnDotsHover: true,
    centerMode: true,

    responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 2,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 991,
          settings: {
            slidesToShow: 2,
            variableWidth:true,
            slidesToScroll: 2
          }
        },
        {
          breakpoint: 767,
          settings: {
            slidesToShow: 2,
            variableWidth:true,
            slidesToScroll: 2
          }
        },
        {
          breakpoint: 640,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
            variableWidth:true
          }
        },
        {
          breakpoint: 320,
          settings: {
            slidesToShow: 1,
            arrows: false,
            variableWidth:true,
            slidesToScroll: 1,
          }
        }
      ]
      
  });

  $('.slider__inner-2').slick({
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: false,
    fade: true,
    asNavFor: '.slider__inner_nav'
  });

  $('.slider__inner_nav').slick({
    slidesToShow: 3,
    slidesToScroll: 1,
    asNavFor: '.slider__inner-2',
    dots: true,
    centerMode: true,
    focusOnSelect: true,
    variableWidth:true,
    responsive: [
      {
        breakpoint: 640,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          arrows: false,
          variableWidth:true
        }
      },
      {
        breakpoint: 320,
        settings: {
          slidesToShow: 1,
          arrows: false,
          variableWidth:true,
          slidesToScroll: 1,
        }
      }
    ]
    
  });

  $("a[href=#up]").click(function(){
    var _href = $(this).attr("href");
    $("html, body").animate({scrollTop: $(_href).offset().top+"px"});
    return false;
  });

  $(window).scroll(function(){
    if($(this).scrollTop()>800){
      $('.call').fadeIn();
    }
    else{
        $('.call').fadeOut();
    }
   });

   $('.header__burger').click(function(event){
    $('.header__items, .header__burger').toggleClass('active');
   });
});